


const episode1Url= 'data:image/jpeg;base64,'
 
const episode2Url= 'data:image/jpeg;base64,'

const episode3Url=  'data:image/jpeg;base64,'

const episode4Url=  'data:image/jpeg;base64,'

const episode5Url=  'data:image/jpeg;base64,'

const episode6Url=  'data:image/jpeg;base64,'




const header = document.querySelector('#header')
const searchInput = document.querySelector('#searchInput');
const container = document.querySelector('#container')


let charactersArr = [];
let epNames = [];
// console.log(epNames)


    fetch(`https://swapi.info/api/films`)   //${target.dataset.id}`)
    .then(r => r.json())
    .then(r => regulate(r))

function regulate(r){
    for (const e of r) {
        let { title, episode_id } = e;
        obj = {
            n:title,
            i:episode_id
        }
        epNames.push(obj)
    }
    // console.log(epNames)
}






searchInput.addEventListener('input', event => {
    const value = event.target.value;
    // const searchList = document.querySelectorAll('.cDiv');
    const searchList = document.querySelectorAll('.cDiv');
    if(value){
    const searchListLength = searchList.length;
    let n = 0;
    //console.log(searchListLength) //82
    // console.log('test ',searchList);
    for (const e of searchList) {
        if (!( e.dataset.name.includes(value) || e.dataset.height.includes(value) || e.dataset.hair_color.includes(value) || e.dataset.skin_color.includes(value) || e.dataset.gender.includes(value) )){ 
            e.classList.add('hide');
            n++;

            if(n == searchListLength){
                const p = document.createElement('p');
                p.style.fontSize= '80px';
                p.innerText="Nothing Found";
                searchList[0].parentElement.appendChild(p);
            } else {if(searchListLength<searchList[0].parentElement.children.length){
                searchList[0].parentElement.lastChild.remove();
            }}


        } else {
            e.classList.remove('hide');
        }
    }
    // console.log('test ',searchList);
    } else {
    for (const e of searchList) {
        
            e.classList.remove('hide');
        }
    }
    });







document.addEventListener('DOMContentLoaded', getCharacterNames);
function getCharacterNames(){
fetch('https://swapi.info/api/people')
    .then(res => res.json())
    .then(ppl => printPeople(ppl))
}

function printPeople(ppl) {
    const div = document.createElement('div');
    for (let i = 0; i < ppl.length; i++) {
        const div2 = document.createElement('div');
        div2.className = 'cDiv';
        const h2 = document.createElement('h2');
        const { name, height, hair_color, skin_color, gender, films } = ppl[i];
        div2.dataset.films = films;
        div2.dataset.name = name;
        div2.dataset.height = height;
        div2.dataset.hair_color = hair_color;
        div2.dataset.skin_color = skin_color;
        div2.dataset.gender = gender;
        charactersArr.push(name);
        h2.innerText = `Character No${i + 1} is: ${name}`;
        div2.appendChild(h2);
        div.appendChild(div2)
        h2.addEventListener('click', showFilms)
    }
    container.appendChild(div);
    // console.log(charactersArr)
}

function showFilms(e) {
    const target = e.target;

    if (!target.parentElement.classList.contains('got-films')) {
        const textDiv = document.createElement('div');
        textDiv.classList.add('text-div');
        const h2 = document.createElement('h2');
        const p = document.createElement('p');
        p.style.fontSize = "24px";
        p.innerText = `PERSONAL TRAITS:
        Height: ${target.parentElement.dataset.height}
        Hair Color: ${target.parentElement.dataset.hair_color}
        Skin Color: ${target.parentElement.dataset.skin_color}
        Gender: ${target.parentElement.dataset.gender}`;


        let series = "";
        let seriesArr = [];
        let films = target.parentElement.dataset.films;
        if (films) {
            films = films.split(',');
        }
        

        
        for (let i = 0; i < films.length; i++) {

            seriesArr.push(films[i].charAt(films[i].length - 1))
                            let episodeName;
                for (const e of epNames) {
                    if ( e.i == seriesArr[i] ) {
                        episodeName= e.n;
                        break;
                    }
                }
            if (i < films.length - 1) {


                series += films[i].charAt(films[i].length - 1) +': '+episodeName+ ', ';
            } else { series += films[i].charAt(films[i].length - 1)+': '+episodeName+ '.' }

        }

        // for (const i of films) {
        //     // console.log('i ',i)
        //     series += i.charAt(i.length-1)+', ';
        // }
        h2.innerText = `appeared in films: ${series}`;
        const filmsContainer = document.createElement('div');
        filmsContainer.classList.add('filmsContainer');
        target.parentElement.appendChild(filmsContainer);
        textDiv.appendChild(p);
        textDiv.appendChild(h2);
        filmsContainer.appendChild(textDiv);
        
        // console.log(seriesArr)
        posterUrls = {
                1:episode1Url,
                2:episode2Url,
                3:episode3Url,
                4:episode4Url,
                5:episode5Url,
                6:episode6Url
            }
        for (let n of seriesArr) {
            const eDiv = document.createElement('div');
            eDiv.classList.add('eDiv');
            eDiv.dataset.id = n;

            const a = document.createElement('a');
            a.href = '#';
            a.innerText = `episode ${n}`;

            const img = document.createElement('img');
            img.src = posterUrls[n];
            

            eDiv.appendChild(a);//link
            eDiv.appendChild(img);//image
            eDiv.addEventListener('click', onEpisodeClick);
            filmsContainer.appendChild(eDiv);
        }
        target.parentElement.classList.toggle('got-films')
    } else {
        // target.parentElement.children[1].classList.toggle('hide')
        // style="display: none;"
        if(!target.parentElement.children[1].style.display){
            target.parentElement.children[1].style="display: none;"
        }else{target.parentElement.children[1].style.display=''}
        // target.parentElement.children[1].style="display: none;"
        // console.log(target.parentElement.children[1].style.display)
    }
}

function onEpisodeClick(e){
    e.preventDefault();
    // console.log('episod clicked');
    let target = e.target;
    let name = target.tagName;
    // console.log(name);
    if(!target.hasChildNodes() || name == 'A'){
        target = target.parentElement;
    }
    name = target.tagName;
    // name = target.tagName;
    // console.log(name);
    // console.log(target.dataset.id);
    if(name=='DIV' && target.dataset.id){
    fetch(`https://swapi.info/api/films`)   //${target.dataset.id}`)
    .then(r => r.json())
    .then(r => addEpisodeDesc(target,r))
    // .then(r => console.log(r))
    }
}
function addEpisodeDesc(target,r){
    for (let e of r) {
        if(e.episode_id == target.dataset.id)
        {r=e;break}
    }
    let {title, episode_id, opening_crawl,director,producer,release_date,characters, planets} = r;
    const div = document.createElement('div');
    div.classList.add('episodeDescContainer');
    const h2 = document.createElement('h2');
    h2.innerText = `EPISODE ${episode_id}`
    const p = document.createElement('p');
    p.innerText = `title: ${title}
    episode_id: ${episode_id}
    opening crawl: ${opening_crawl}
    director: ${director}
    producer: ${producer}
    release_date: ${release_date}`
    let c = parseCharacters(characters);
    const p2 = document.createElement('h3');
    p2.innerText = 'Characters:';

    // 
    // planets: ${planets};
    div.appendChild(h2)
    div.appendChild(p)
    div.appendChild(p2)
    for (let e of c) {
        let {name,link} = e;
        const divChar = document.createElement('div');
        divChar.classList.add('divChar');
        divChar.dataset.link = link;
        const a = document.createElement('a');
        a.href = "#";
        a.innerText = name;
        divChar.appendChild(a);
        div.appendChild(divChar);
        divChar.addEventListener('click', getFilmsByC);
    }
    target.after(div);


}

function parseCharacters(characters){
    let res = [];
    for (let i = 0; i < characters.length; i++) {
        let ind = characters[i].lastIndexOf('/');
        let val = characters[i].substring(ind+1);
        // console.log(val)
        let obj ={
            name:charactersArr[val-1],
            link:characters[i]
        }
        res.push(obj);
    }
    return res;
}
async function getFilmsByC(e){
    e.preventDefault();
    // console.log('char clicked');
    let target = e.target;
    let name = target.tagName;
    // console.log(name);
    if(!target.hasChildNodes() || name == 'A'){
        target = target.parentElement;
    }
    let obj;
    const res = await fetch(target.dataset.link)

    obj = await res.json();

    let {films} = obj;
    // console.log('films got', typeof films,films);
    let seriesArr=[];
    let series = '';
        for (let i = 0; i < films.length; i++) {

            seriesArr.push(films[i].charAt(films[i].length - 1))
                            let episodeName;
                for (const e of epNames) {
                    if ( e.i == seriesArr[i] ) {
                        episodeName= e.n;
                        break;
                    }
                }
            if (i < films.length - 1) {


                series += films[i].charAt(films[i].length - 1) +': '+episodeName+ ', ';
            } else { series += films[i].charAt(films[i].length - 1)+': '+episodeName+ '.' }

        }
    const h2 = document.createElement('h2');
    h2.innerText = `appeared in films: ${series}`;

    target.appendChild(h2)

}


